mapper.select:
        SELECT


--         级联表的字段

        FROM kecheng  a



                and a.id in

                #{item}



                and a.kecheng_name like CONCAT('%',#{params.kechengName},'%')


                and a.kecheng_types = #{params.kechengTypes}


                and a.kecheng_laoshi like CONCAT('%',#{params.kechengLaoshi},'%')


                  and UNIX_TIMESTAMP(a.kaike_time) >= UNIX_TIMESTAMP(#{params.kaikeTimeStart}) 


                  and UNIX_TIMESTAMP(a.kaike_time) <= UNIX_TIMESTAMP(#{params.kaikeTimeEnd}) 


                and a.shangke_shijian like CONCAT('%',#{params.shangkeShijian},'%')


                  and a.kecheng_clicknum >= #{params.kechengClicknumStart}   


                  and a.kecheng_clicknum <= #{params.kechengClicknumEnd}   


                and a.kecheng_clicknum = #{params.kechengClicknum}


                and a.shangxia_types = #{params.shangxiaTypes}


                  and a.kecheng_delete >= #{params.kechengDeleteStart}   


                  and a.kecheng_delete <= #{params.kechengDeleteEnd}   


                and a.kecheng_delete = #{params.kechengDelete}


                and a.kecheng_content like CONCAT('%',#{params.kechengContent},'%')


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 




        order by a.${params.orderBy} desc
